import React from 'react';
import { Link } from 'react-router-dom';
import './../css/NavigationBar.css'; // Include CSS for styling

const NavigationBar = () => {
  const token = localStorage.getItem('token'); // Check if the user is logged in

  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove the token from localStorage
    localStorage.removeItem('role'); // Remove the role if stored
    window.location.href = '/'; // Redirect to the login page
  };

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <Link to="/" className="navbar-logo">
          Hiru Capital Investment
        </Link>
      </div>
      <ul className="navbar-links">
        {token ? (
          // Show Logout button if the user is logged in
          <li>
            <button className="navbar-link logout-button" onClick={handleLogout}>
              Logout
            </button>
          </li>
        ) : (
          // Show Login and Register links if the user is not logged in
          <>
            <li>
              <Link to="/" className="navbar-link">
                Login
              </Link>
            </li>
            <li>
              <Link to="/register" className="navbar-link">
                Register
              </Link>
            </li>
          </>
        )}
      </ul>
    </nav>
  );
};

export default NavigationBar;
